import streamlit as st
import pandas as pd
import time
import snscrape.modules.twitter as sntwitter
import pymongo

rad = st.sidebar.radio("MAINMENU",['HOME','MENU','ABOUT US ?','VERSION'],index=0)
if rad =="HOME":
    st.image("data:image/jpeg;base64,")
    st.title("TWITTER SCRAPER APP")
    st.header("Welcome's You!:heart::smile:")
    st.subheader("Let start scrape data from twitter:sunglasses:")
    st.markdown("""See menu for more options""",True)
    progress = st.progress(0)
    for i in range(100):
        time.sleep(0.1)
        progress.progress(i + 1)
    st.balloons()

if rad=="MENU":
   st.image("data:image/jpeg;base64,")
   st.title("TWITTER SCRAPER APP")
   st.header("Welcome's You!:heart:")
   st.subheader("Let start scrape data from twitter")
   keyword = st.text_input("Enter keyword to extract data")
   no_of_tweets = st.slider("How many tweets do you want to get from twitter ?", 10, 600, step=10)
   starting_date=st.date_input("Enter date from(Format: YYYY-MM-DD)")
   ending_date = st.date_input("Enter date to(Format :YYYY-MM-DD)")
   output_csv = st.radio("Do you want download file ", ['YES', 'NO'])
   file_name = st.text_input("Name of csv file")
   st.write("Upload your file in db")
   s3 = st.radio("upload your file ", ['yes', 'no'], index=1)



   submit=st.button("Search")
   tweets = []
   t = f"{keyword} since:{starting_date} until:{ending_date}"

   if submit:
      for i, tweet in enumerate(sntwitter.TwitterSearchScraper(t).get_items()):
          if i > no_of_tweets:
              break
          tweets.append([tweet.date, tweet.id,tweet.user.username, tweet.content, tweet.url, tweet.replyCount, tweet.likeCount, tweet.sourceLabel, tweet.user.location,tweet.lang,tweet.retweetCount,tweet.retweetedTweet])

      df = pd.DataFrame(tweets,columns=['Date', 'ID','username', 'Tweets', 'url', 'Replycount', 'Likecount', 'sourcelabel', 'Location','Language','Retweet count','Retweed Tweet'])

      df.to_csv(f'{file_name}.csv',index=False,encoding='utf=8')
      df.Store_csv=True

      if output_csv == 'YES':
          df.Output = f'{file_name}.csv'
          st.write("saved ")
      st.dataframe(df)
      g = df.to_dict("r")


      if s3 == 'yes':
          st.write("upload successfully:smile::heart:")
          client = pymongo.MongoClient("mongodb://localhost:27017/")
          mydb = client["tsns"]
          mycol = mydb["twit"]
          mycol.insert_many(g)

          st.markdown(g)


if rad=="ABOUT US ?":
    st.image("data:image/jpeg;base64,")
    st.write(":star:It is purely developed in streamlit")
    st.write(":star:It is used to extract data from twitter ")
    st.write(":star:It is easy to handle ")
    st.write(":star:It is easy to download and save in your folder ")
    st.write(":star:you can also upload your file in database easily")

if rad=="VERSION":
    st.write(":bird:Version 1.1")
    st.write(":bird:It is devloped in streamlit using python")
    st.balloons()










